INSERT INTO `wl_associations` (`id`, `phone`, `phone1`, `address`, `email`, `content`, `create_time`, `update_time`) VALUES
(1, '17630968036', '', '南阳南阳南阳南阳南阳', '17630968036@qq.com', '<p><span style=\"color: rgb(6, 6, 6);\">2007年，北京每日徒步运动中心发起首届全民徒步日活动，社会各界以\"健康、和谐、绿色\"的名义积极响应徒步日活动。</span><span style=\"color: rgb(255, 76, 65);\">著名户外登山领军人物王永峰在全民徒步日倡议书上签名</span><span style=\"color: rgb(6, 6, 6);\">。</span></p><p style=\"text-align: center;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20180903/20180903095524_40735.jpg\" alt=\"\"></p><p style=\"text-align: center;\"><span style=\"color: rgb(6, 6, 6);\">&nbsp; 活动发起方负责人金乔主任表示，\"</span><span style=\"color: rgb(6, 6, 6);\"><span style=\"color: rgb(255, 76, 65);\">开展全民徒步日活动，并收集社会各界签名，目的很单纯，希望通过花费不是很高的大型活动，让普通老百姓了解徒步，了解健身的重要意义；让普通百姓重新回归自然，实现心灵纯洁、社会和谐的目标。为此，北京每日徒步运动中心愿意一直努力下去……</span></span><span style=\"color: rgb(6, 6, 6);\">\"。随着全民徒步日的影响越来越大，各地户外俱乐部也纷纷加入，呼应北京全民徒步日活动。全国就此掀起徒步风潮。</span></p><p style=\"text-align: center;\"><img src=\"http://www.chinawalking.net.cn/UploadFile/image/20180903/20180903095524_79258.jpg\" width=\"600\" height=\"398\" alt=\"\"></p>', '2019-02-27 00:00:00', '2019-02-28 21:26:39');

