CREATE TABLE `wl_notices` (
  `id` int(3) NOT NULL,
  `title` varchar(100) NOT NULL COMMENT '公告标题',
  `content` text NOT NULL COMMENT '公告内容',
  `create_time` datetime DEFAULT NULL,
  `update_time` datetime DEFAULT NULL
