--
-- 转存表中的数据 `wl_notices`
--

INSERT INTO `wl_notices` (`id`, `title`, `content`, `create_time`, `update_time`) VALUES
(3, '【户外资讯】《户外活动突遇塌方、泥石流如何避险逃生？》', '<p><span style=\"color: rgb(51, 51, 51); font-size: 16px;\">摄取足够的能量外，维生素的供给不可缺少，特别应注意每天补充适量的维生素A、维生素B及维生素D。另外，食物应易于消化，少食含粗纤维和易产气的食物（芹菜、韭菜、大豆等），多吃碱性食物、蔬菜、水果、海带等，以利于体内酸碱平衡，增加碱的储备，提高运动能力。另外，冬季出行一定要带一些常用药品（如感冒药、咳嗽药、消炎药、热帖等）。9、团队互帮互助。结伴可以互相照顾，行进时，后面队员应尽量踩着前面队员的脚印前进。一则为了安全；二来可以节省不少体力；遇到大雪覆盖、无路可寻时，身体强壮的队员应交替轮换开路，以保证整个队伍的平均体能。雪地徒步的关键是慢和稳，少走、慢走都没关系，一定不能急于赶路。10、防止雪盲。雪镜或墨镜可以防止雪盲，如果都是黑色的松林，多看看森林，会防止雪盲。 注意观察积雪情况，预防因雪质疏松发生雪崩，造成登山人员伤亡。</span><br></p>', '2019-02-27 00:00:00', '2019-03-13 18:31:42'),
(2, '大胆地到山下活动，所以不要乱扔食物', '<p><span style=\"color: rgb(51, 51, 51); font-size: 16px;\">往往会不顾人类活动的威胁，大胆地到山下活动，所以不要乱扔食物，否则容易招来野兽，无论是野猪、熊，对人来说，都是不幸的。15、徒步结束后，要用热水加盐泡脚，以解除疲劳。脚掌有水泡时，可用针(先用酒精棉球擦一下或在火上烧一下)穿孔引出水，再涂上红药水，防止感染。切记不要将皮撕下，这样即容易感染更会加重脚部的疼痛。二、冬季徒步登山必备装备清单1、高帮登山（徒步）鞋：冬季雪地穿越时，对登山（徒步）鞋的防水透气性能要求很高。</span><br></p>', '2019-02-26 20:04:03', '2019-03-13 18:31:45'),
(4, '在“玩中学”的户外逃生教育，势在必行！', '<p><span style=\"color: rgb(51, 51, 51); font-size: 16px;\">1、小心滑坠。冬季山地积雪很多，相对来说，积雪路段是安全的。但是有很多积雪路面被长期踩踏后形成冰层，非常光滑，滑倒的几率很大。这时在脚上套上行走冰爪可以有效防止滑倒。12、露营须知。观察注意附近有无雪崩的可能、树枝上的积雪是否会被风吹落。雪地露营要备好足够保暖的睡袋，使用稍厚一些的防潮垫或者使用自动充气防潮垫。要始终保留一套干衣和一双袜子备用。 露营前，用雪橇或雪鞋踩实出一块平地，固定好帐篷。最好使用带有雪裙的雪地帐。晚上在帐篷里戴好帽子和手套。身体的大部分热量是通过头部散发的，同时保持手指的温度和灵活是相当重要的。帐篷结露现象。帐篷内外温差较大，地面上蒸发的水珠会到帐篷上凝结成小水珠，帐篷上就会出现结露的现象。如果发生此现象，可以用干燥的毛巾擦干，经常换气和铺垫防水布，可以一定程度上解决结露问题。切记在寒冷的环境下也不要将帐篷全部封死，不然有呼吸引起的水分会因无法会散而结在帐篷内壁，形成雪和滴水。13、必须做好保温。冬季在户外所面临的最大危险来自于徒</span><br></p>', '2019-02-20 00:00:00', '2019-03-13 18:31:39'),
(5, '做个有前途的吃货 黑石B011便携式多功能户外烧烤炉简介', '<p><span style=\"color: rgb(51, 51, 51); font-size: 16px;\">环境下也不要将帐篷全部封死，不然有呼吸引起的水分会因无法会散而结在帐篷内壁，形成雪和滴水。13、必须做好保温。冬季在户外所面临的最大危险来自于徒步和露营中的低温，严防冻伤和失温（失温往往出现得很快）。休息时，不要坐在雪地上和风口处，出汗时可稍松衣领，不要脱衣摘帽，以防伤风受寒。手、脸长期暴露在外，或者长时间接触冰雪后，就会补冻伤。如果冻伤后不可揉搓冻伤部伤，也不可用雪摩擦或用凉水浸泡。可以用温暖的身体部位，如手、腹部等让受冻的组织升温。最好多穿人造毛或纯羊毛织物。纯棉织物是致命的，因为棉织品一旦打湿则失去保温性能，且很难烘干。14、小心野生动物。冬季山区有很多没有冬眠的野生动物，因为冬季食物缺乏，往往会不顾人类活动的威胁，大胆地到山下活</span><br></p>', '2019-02-20 00:00:00', '2019-03-13 18:31:35');

